# factory pattern example 

class Shape:
    sides = 0

    def type(self):
        print("This is an abstract class")

class Triangle(Shape):
    sides = 3
    def type(self):
        print("This is a triangle")

class Rectangle(Shape):
    sides = 4
    def type(self):
        print("This is a rectangle")

class Factory:
    def get_obj(self, shape):
        if shape == "triangle":
            return Triangle()
        if shape == "rectangle":
            return Rectangle()

