def is_anagram(word1, word2):
    word1 = word1.replace(' ', '').lower()
    word2 = word2.replace(' ', '').lower()

    if len(word1) != len(word2):
        return False

    count = {}
    for letter in word1:
        if letter in count:
            count[letter] += 1
        else:
            count[letter] = 1

    for letter in word2:
        if letter in count:
            count[letter] -= 1
        else:
            count[letter] = 1

    for k in count:
        if count[k] != 0:
            return False

    return True

