def levenshtein_distance(s1: str, s2: str):
    m = len(s1)
    n = len(s2)

    table = dict()
    for i in range(m+1):
        table[i, 0] = i
    for j in range(n+1):
        table[0, j] = j

    for i in range(1, m+1):
        for j in range(1, n+1):
            if s1[i-1] == s2[j-1]:
                cost = 0
            else: 
                cost = 1
            table[i, j] = min(table[i-1, j] + 1, 
                    table[i, j-1] + 1, 
                    table[i-1, j-1] + cost)

    return table[m, n]

