def neighbors(cell):
 x, y = cell
 nb = [(x-1, y-1), (x, y-1), (x+1, y-1), (x-1, y), (x+1, y), (x-1, y+1), (x, y+1), (x+1, y+1)]
 return [nb for nb in nb if 0 <= nb[0] < 8 and 0 <= nb[1] < 8]

def conway(world):
 new_world = set()
 for cell in world:
  total = sum(1 for neighbor in neighbors(cell) if neighbor in world)
  if (total == 3) or (total == 2 and cell in world):
   new_world.add(cell)
 return new_world

def main():
 world = {(0,0), (0, 1), (1, 0)}
 print('Initial World:', *world)
 while True:
  world = conway(world)
  print('Next World:', *world)

