def is_armstrong(num):
   # calculate the total of each digits power to the length of digits 
    temp = num
    order = len(str(num))
    sum = 0
    while temp > 0:
        digit = temp % 10
        sum += digit ** order
        temp //= 10

    if (num == sum):
        return True
    else:
        return False 

