def translate_pig_latin(sentence):
    vowels = "aeiou"
    words = sentence.split()
    new_words = []
    for word in words:
        if word[0] in vowels:
            word += "way"
            new_words.append(word)
        else:
            i = 0
            for letter in word:
                if letter in vowels:
                    break
                i += 1
            new_words.append(word[i:]+ word[:i] + "ay")
    return " ".join(new_words)

