class Hangman:
    def __init__(self):
        self.secret_word = self.get_random_word()
        self.incorrect_guesses = 0
        self.masked_word = ['_'] * len(self.secret_word)
        self.guessed_letters = []

    def get_random_word(self):
        return random.choice(valid_words)

    def guess_letter(self, letter):
        self.guessed_letters.append(letter)
        if letter in self.secret_word:
            for i, letter in enumerate(self.secret_word):
                if letter == self.guessed_letters[-1]:
                    self.masked_word[i] = letter
            return True
        else:
            self.incorrect_guesses += 1
            return False

    def is_game_over(self):
        return self.incorrect_guesses >= MAX_GUESSES or '_' not in self.masked_word

