def compress_string(s): 
    # Count occurences of each character 
    dict = {} 
    for c in s: 
        if c in dict: 
            dict[c] += 1
        else: 
            dict[c] = 1

    # Create a new string with the format 
    # <character><number_of_occurences>
    compressed = [] 
    for c in dict.keys(): 
        compressed.append(c + str(dict[c])) 

    # Join all the characters inside the list 
    return ''.join(compressed)

