def quick_sort(arr):
    if len(arr) < 2:
        return arr
    else:
        pivot = arr[0]
        less = [i for i in arr[1:] if i <= pivot]
        greater = [i for i in arr[1:] if i > pivot]
        return quick_sort(less) + [pivot] + quick_sort(greater)

# Define the function to calculate approximate root using bisection algorithm
def bisection(x, epsilon):
    low = 0
    high = x
    guess = (high + low)/2
    while abs(x - guess**2) > epsilon:
        if guess**2 > x:
            high = guess
        else:
            low = guess
        guess = (high + low)/2
    return guess

print(bisection(15, 0.001))

def fibonacci(n):
    if n == 1:
        return [1]
    elif n == 2:
        return [1, 1]
    else:
        fib_list = fibonacci(n - 1)
        fib_list.append(fib_list[-1] + fib_list[-2])
        return fib_list

