"""
Generate a dictionary that keeps count of how many times each word appears in a given sentence

Parameters:
sentence (str): a sentence

Return:
dict: a dictionary with word as key and count as value
"""

def count_words(sentence):
    word_count = {}
    words = sentence.split()
    for word in words:
        if word not in word_count:
            word_count[word] = 1
        else:
            word_count[word] += 1
    return word_count

if __name__ == '__main__':
    sentence = "the quick brown fox jumps over the lazy dog"
    print(count_words(sentence))

def middle_sum(numbers):
    if len(numbers) == 0:
        return None

    if len(numbers) % 2 == 1:
        return numbers[len(numbers) // 2]
    else:
        return numbers[len(numbers) // 2 - 1] + numbers[len(numbers) // 2]

import math

def quadratic_roots(a, b, c):
    d = (b ** 2) - (4 * a * c) 
    if d < 0:
        return None  # No real roots
    elif d == 0:
        return -b / (2 * a)  # One root 
    else:
        x1 = (-b + math.sqrt(d)) / (2 * a) 
        x2 = (-b - math.sqrt(d)) / (2 * a) 
        return (x1, x2)  # Two roots

print(quadratic_roots(-2, 5, -3))

