 self.S = S
 self.K = K
 self.T = T
 self.r = r
 self.sigma = sigma

 def d1(self):
 return (math.log(self.S/self.K) + (self.r + 0.5 * self.sigma ** 2)*self.T)/(self.sigma * math.sqrt(self.T))

 def d2(self):
 return self.d1() - self.sigma * math.sqrt(self.T)

 def call_price(self):
