def findMaxValue(arr):
    max_val = arr[0]

    for i in range(1, len(arr)):
        if arr[i] > max_val:
            max_val = arr[i]

    return max_val

arr = [3, 4, 6, 2, 8, 9, 5]
maxValue = findMaxValue(arr)
